  static styles = css`
    :host {
      --color-light: #ffffff;
      --color-medium: #777777;
      --color-dark: #333333;
      --color-raksul-C: #00a0e8;
      --color-raksul-M: #e4007e;
      --color-raksul-Y: #fef300;
      --color-hacobell: #0076dd;
      --color-novasell: #00aa15;
      --color-josys: #f8761f;
    }

    svg {
      width: 100%;
      height: auto;
    }
    .raksul,
    .hacobell,
    .novasell,
    .josys {
      opacity: 0;
      &.icon {
        visibility: hidden;
      }
      &.homepage-bg {
        opacity: 1;
      }
      &.button {
        opacity: 0.3;
      }
    }
    .raksul.icon {
      visibility: visible;
    }
    #buttons > *,
    #arrows > * {
      cursor: pointer;
    }
    #arrows > * {
      opacity: 0.5;
      transition: opacity 0.5s ease;
      &:hover {
        opacity: 1;
      }
    }
  `;
